import React from "react"
import { useState } from "react"
import Header from "../../components/header/Header"
import Footer from "../../components/footer/Footer"
import ProjectCard from "../../components/ProjectCard/ProjectCard"
import ProjectsImg from "./ProjectsImg"
import { Fade } from "react-reveal"
import { projectsHeader, projects } from "../../portfolio.js"
import { style } from "glamor"
import "./Projects.css"
import Filtration from "./Filtration"

function Projects(props) {
  const theme = props.theme
  const [data, setData] = useState([
    {
      id: 1,
      label: "WordPress",
      name: "Project 1",
      description: "This is project 1",
    },
    {
      id: 2,
      label: "Frontend",
      name: "Project 2",
      description: "This is project 2",
    },
    {
      id: 3,
      label: "Mobile Apps",
      name: "Project 3",
      description: "This is project 3",
    },
    {
      id: 4,
      label: "WordPress",
      name: "Project 4",
      description: "This is project 4",
    },
    {
      id: 5,
      label: "Frontend",
      name: "Project 5",
      description: "This is project 5",
    },
    {
      id: 6,
      label: "Mobile Apps",
      name: "Project 6",
      description: "This is project 6",
    },
    {
      id: 7,
      label: "Mobile Apps",
      name: "Project 7",
      description: "This is project 7",
    },
  ])

  const [filteredData, setFilteredData] = useState(data)

  const handleFilterChange = (filteredData) => {
    setFilteredData(filteredData)
  }
  const styles = style({
    backgroundColor: `${theme.accentBright}`,
    ":hover": {
      boxShadow: `0 5px 15px ${theme.accentBright}`,
    },
  })

  return (
    <div className="projects-main">
      <Header theme={theme} setTheme={props.setTheme} />
      <Filtration
        data={data}
        setData={setData}
        onFilterChange={handleFilterChange}
      />
      <div className="project-list">
        {filteredData.map((project) => (
          <div key={project.id} className="project">
            <h2>{project.name}</h2>
            <p>{project.description}</p>
            {/* Render other project details here */}
          </div>
        ))}
      </div>
      <div className="repo-cards-div-main">
        {projects.data.map((repo, index) => {
          return <ProjectCard key={"repo-" + index} repo={repo} theme={theme} />
        })}
      </div>
      <br />
      <br />
      <br />
      <a
        {...styles}
        className="general-btn more-projects-btn"
        href="https://github.com/sumit-sharma-02/"
      >
        More Projects (Github)
      </a>
    </div>
  )
}

export default Projects
