        {filteredProjects.map((project) => (
          <ProjectCard key={project.id} project={project} />
        ))}
      </div>

      <br />
      <br />
      <br />
      <a
        {...styles}
        className="general-btn more-projects-btn"
        href="https://github.com/sumit-sharma-02/"
      >
        More Projects (Github)
      </a>
    </div>
  )
}

export default Projects
