import React, { useState, useEffect } from "react"
import "./Filtration.css"
import ProjectCard from "./ProjectCard"
const Filtration = ({ data }) => {
  const [selected, setSelected] = useState("All")

  useEffect(() => {
    setSelected("All")
  }, [data])

  const handleClick = (label) => {
    setSelected(label)
  }

  const filterProjects = (project) => {
    if (selected === "All") {
      return true
    } else {
      return project.labels.includes(selected)
    }
  }

  const projectsToDisplay = data.filter(filterProjects)

  return (
    <div className="filtration-container">
      <div className="labels-container">
        <div
          className={`label ${selected === "All" ? "selected" : ""}`}
          onClick={() => handleClick("All")}
        >
          All
        </div>
        {data
          .flatMap((project) => project.labels)
          .reduce((acc, label) => {
            if (!acc.includes(label)) {
              acc.push(label)
            }
            return acc
          }, [])
          .map((label) => (
            <div
              key={label}
              className={`label ${selected === label ? "selected" : ""}`}
              onClick={() => handleClick(label)}
            >
              {label}
            </div>
          ))}
      </div>
      <div className="project-list">
        {projectsToDisplay.map((project) => (
          <ProjectCard key={project.id} project={project} />
        ))}
      </div>
    </div>
  )
}

export default Filtration
