import React, { useState } from "react"

const labels = ["Label 1", "Label 2", "Label 3"]

const Filtration = ({ data, setData }) => {
  const [selectedLabel, setSelectedLabel] = useState("")

  const handleLabelClick = (label) => {
    if (selectedLabel === label) {
      setSelectedLabel("")
      setData(data) // Reset the data to its original state
    } else {
      setSelectedLabel(label)
      setData(data.filter((item) => item.label === label)) // Filter the data based on the selected label
    }
  }

  return (
    <div className="filtration">
      {labels.map((label) => (
        <div
          key={label}
          className={`label ${selectedLabel === label ? "selected" : ""}`}
          onClick={() => handleLabelClick(label)}
        >
          {label}
        </div>
      ))}
    </div>
  )
}

export default Filtration
