import React, { useState, useEffect } from "react"
import "./Filtration.css"
import ProjectCard from "./ProjectCard"
const Filtration = ({ data }) => {
  const [labels, setLabels] = useState([])

  useEffect(() => {
    // extract unique labels from data
    const uniqueLabels = Array.from(
      new Set(data.flatMap((project) => project.labels))
    )
    setLabels(uniqueLabels)
  }, [data])

  const [selectedLabel, setSelectedLabel] = useState("")

  const handleLabelClick = (label) => {
    setSelectedLabel(label)
  }

  return (
    <div className="filtration-container">
      <div className="label-container">
        {labels.map((label, index) => (
          <div
            key={index}
            className={`label ${selectedLabel === label ? "active" : ""}`}
            onClick={() => handleLabelClick(label)}
          >
            {label}
          </div>
        ))}
      </div>
      <div className="project-list">
        {data
          .filter((project) =>
            selectedLabel === "" ? true : project.labels.includes(selectedLabel)
          )
          .map((project) => (
            <ProjectCard key={project.id} project={project} />
          ))}
      </div>
    </div>
  )
}

export default Filtration
