import React from "react"
import { Fade, Flip } from "react-reveal"
import "./DegreeCard.css"

function DegreeCard(props) {
  const degree = props.degree
  const theme = props.theme
  return (
    <div className="degree-card">
      {degree.logo_path && (
        <Flip left duration={2000}>
          <div className="card-img">
            <img
              style={{
                maxWidth: "100%",
                maxHeight: "100%",
                transform: "scale(0.9)",
              }}
              src={require(`../../assets/images/${degree.logo_path}`)}
              alt={degree.alt_name}
            />
          </div>
        </Flip>
      )}
      <Fade right duration={2000} distance="40px">
        <div
          className="card-body"
          style={{
            width: degree.logo_path ? "90%" : "100%",
            backgroundColor: theme.themeColor,
          }}
        >
          <div className="body-header" style={{ backgroundColor: red }}>
            <div className="body-header-title">
              <span className="card-title" style={{ color: theme.ternaryText }}>
                {degree.title}
              </span>
              <span
                className="card-subtitle"
                style={{ color: theme.ternaryText }}
              >
                {degree.subtitle}
              </span>
            </div>
            <div className="body-header-duration">
              <span className="duration" style={{ color: theme.ternaryText }}>
                {degree.duration}
              </span>
            </div>
          </div>
          <div className="body-content">
            {degree.descriptions.map((sentence, index) => {
              return (
                <p
                  key={"degree-" + index}
                  className="content-list"
                  style={{ color: theme.text }}
                >
                  {sentence}
                </p>
              )
            })}
          </div>
        </div>
      </Fade>
    </div>
  )
}

export default DegreeCard
